      .shape('fill')
      .active(true)
      .style({
        opacity: 1
      });
    const layer2 = new LineLayer({
      zIndex: 2
    })
      .source(data)
      .color('#fff')
      .size(0.8)
      .style({
        opacity: 1
      });

    scene.addLayer(layer);
    scene.addLayer(layer2);
  });

