import { Scene, HeatmapLayer } from '@antv/l7';
import { GaodeMap } from '@antv/l7-maps';

const scene = new Scene({
  id: 'map',
  map: new GaodeMap({
    style: 'light',
    pitch: 0,
    center: [ 107.054293, 35.246265 ],
    zoom: 4.056
  })
});

fetch(
  'https://gw.alipayobjects.com/os/basement_prod/7359a5e9-3c5e-453f-b207-bc892fb23b84.csv'
)
  .then(res => res.text())
  .then(data => {
    const layer = new HeatmapLayer({})
      .source(data, {
        parser: {
          type: 'csv',
          x: 'lng',
          y: 'lat'
        },
        transforms: [
          {
            type: 'grid',
            size: 20000,
            field: 'v',
            method: 'sum'
          }
        ]
      })
      .shape('square')
      .style({
        coverage: 1,
        angle: 0
      })
      .color(
        'count',
        [
          '#0B0030',
          '#100243',
          '#100243',
          '#1B048B',
          '#051FB7',
          '#0350C1',
          '#0350C1',
          '#0072C4',
          '#0796D3',
          '#2BA9DF',
          '#30C7C4',
          '#6BD5A0',
          '#A7ECB2',
          '#D0F4CA'
        ].reverse()
      );

    scene.addLayer(layer);
  });

