      .active(true)
      .color('name', [ '#5B8FF9', '#5CCEA1', '#5D7092', '#F6BD16', '#E86452' ])
      .style({
        opacity: 0.3,
        strokeWidth: 2
      });

    scene.addLayer(pointLayer);
  });

